import * as types from './../constants/ActionType';
import * as configs from './../constants/Config';

let defaultState = {
	style:'info',
	title:'',
	content:''
};

const notify = (state = defaultState, action) => {
	switch(action.type){
		case types.CHANGE_NOTIFY:
			return {...state}
		default:
			return state;
	}
}


export default notify
