import React, { Component } from 'react';
import {connect} from 'react-redux';
import TaskFinishItem from './../Components/TaskFinishItem';
import {tasksCompletedRef} from './../firebase';

class TaskFinishList extends Component {
  constructor(props){
    super(props);
    this.state={
      items:[],
      isMounted: false
    }
  }
  componentDidMount(){
    this.getTaskFinish();
    this.setState({isMounted: true})
  }
  getTaskFinish(){
    tasksCompletedRef.on('value',items =>{
      let data = [];
    items.forEach(item=>{
      const {email,name} = item.val();

      data.push({email,name,key:item.key})
    })
    this.setState({
      items:data
    })
    })
  }
  componentWillUnmount(){
    this.state.isMounted = false
}

    render() {
      let {items} = this.state;  

        return (

                 <div className="panel panel-success">
                  <div className="panel-heading">
                    <h3 className="panel-title">Task Finish</h3></div>
                  <div className="panel-body">
                   {this. showElementBody(items)}
                  </div>

                </div>
        );
    }
    showElementBody(items){
      let xhml = null;
      if(items.length >0){
        xhml = items.map((item,index)=>{

            return  <TaskFinishItem  key={index} item={item} index={index}/>

        })
        return xhml 
      } else {
        return <h4>Loading</h4>}
    }
}


export default TaskFinishList;
