import * as types from './../constants/ActionType'; 
let defaultState={
isLogin:false,
info:{email:'',uid:'', website:''}
}

const user =  (state = initialState, action) => {
    switch (action.type) {

    case types.USER_LOGIN:
        return { ...state }
        case types.USER_LOGOUT:
            return { ...state }
    default:
        return state
    }
}
export default user;
